(678, '2005-10-08 00:00:00', 'Musique', 'adresse', '', '', '<table class="liste_musicale">\r\n	<tbody>\r\n		<tr>\r\n			<td class="liste_musicale_titre" colspan="3">\r\n				musique</td>\r\n		</tr>\r\n		<tr class="liste_musicale_contenu">\r\n			<td class="liste_musicale_photo">\r\n				<img src="image/pour_page_independante/soupe.jpg" /></td>\r\n			<td class="liste_musicale_playlist">\r\n				<div class="liste_musicale_playlist_musique">\r\n					<div class="liste_musicale_playlist_musique_titre">\r\n						<p>\r\n							&Agrave; trop vouloir para&icirc;tre</p>\r\n					</div>\r\n					<div class="liste_musicale_playlist_musique_player">\r\n						<object data="dewplayer-multi.swf?son=musique/soupe/01.mp3" height="20" type="application/x-shockwave-flash" width="200"><param name="movie" value="dewplayer-multi.swf?son=musique/soupe/01.mp3" /></object></div>\r\n				</div>\r\n				<div class="liste_musicale_playlist_musique">\r\n					<div class="liste_musicale_playlist_musique_titre">\r\n						<p>\r\n							Les pr&eacute;jug&eacute;s</p>\r\n					</div>\r\n					<div class="liste_musicale_playlist_musique_player">\r\n						<object data="dewplayer-multi.swf?son=musique/soupe/02.mp3" height="20" type="application/x-shockwave-flash" width="200"><param name="movie" value="dewplayer-multi.swf?son=musique/soupe/02.mp3" /></object></div>\r\n				</div>\r\n				<div class="liste_musicale_playlist_musique">\r\n					<div class="liste_musicale_playlist_musique_titre">\r\n						<p>\r\n							Merci pour l`h&eacute;ritage</p>\r\n					</div>\r\n					<div class="liste_musicale_playlist_musique_player">\r\n						<object data="dewplayer-multi.swf?son=musique/soupe/03.mp3" height="20" type="application/x-shockwave-flash" width="200"><param name="movie" value="dewplayer-multi.swf?son=musique/soupe/03.mp3" /></object></div>\r\n				</div>\r\n			</td>\r\n			<td class="liste_musicale_description">\r\n				<p>\r\n					<img alt="Mychèle Nolet, Odette Lebert, Pierre\r\n	Massie, Karine Daviau, Anne-Marie Dinelle" src="image/pour_page_independante/soupierre/cd.jpg" />Un gros merci &agrave; Odette Lebert pour avoir ramass&eacute; 1000$ pour la soupierre en vendant 200 copies du CD intitul&eacute; &quot;MERCI POUR TA SOUPE, PIERRE&quot;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '', '', '0000-00-00 00:00:00', 'news', 'complet'),
