INSERT INTO `poste` (`id`, `personne`, `poste`, `type_poste`, `adresse`) VALUES
INSERT INTO `topic` (`id`, `titre_du_topic`, `lien_du_topic`, `description_du_topic`, `image_du_topic`, `id_de_categorie_du_topic`) VALUES
(73, 'Stage humanitaire', 'page.php?page_numero=12', '<p>\r\n	Implique entre 8 et 15 personnes dans un voyage d&#39;entraide humanitaire en R&eacute;publique Dominicaine. Ces jeunes stagiaires pr&eacute;parent aussi le voyage ensemble et les activit&eacute;s de financement.</p>\r\n', '73.jpg', '7'),
(13, 'Ciné Débat', '../cine_debat/pp/wordpress/', 'c''est un groupe de personnes qui visionnent et organisent des visionnement de films qui pousse la réflexion et qui alimentent des débats et qui peuvent faire découvrir des sujets d''intérêts généraux.', '13.jpg', '7'),
(12, 'Artisanat', 'page.php?page_numero=34', 'Le mardi, une équipe fait de l''artisanat au Petit Peuple. de nombreuses réalisations sont en cours', '12.jpg', '7'),
(72, 'Agir ensemble', 'page.php?page_numero=48', 'Agir Ensemble est un stage d''insertion au travail. Son rôle est d''aider ses 8 stagiaire rénuméré par le gouvernement à prendre place à l''école ou au travail tout en aidant dans la communauté.', '72.jpg', '7'),
(27, 'Journée corvée 2007', 'page.php?page_numero=19', '', '27.jpg', '9'),
(16, 'Journée corvée', 'page.php?page_numero=16', '<p>\r\n	cette journ&eacute;e est l&#39;occasion de remercier le centre notre dame de la rouge (pour le l&#39;&eacute;bergement de nos camps) en lui rendant des services en &eacute;quipe comme laver les locaux, rammasser les feuilles et d&#39;autres travaux.</p>\r\n', '16.jpg', '7'),
(74, 'Noël d''enfants 2005', 'page.php?page_numero=23', '', '74.jpg', '12'),
(75, 'Noël d''enfants 2006', 'page.php?page_numero=24', '', '75.jpg', '12'),
(76, 'Camp de lancement 2008', 'page.php?page_numero=33', 'Grâce au centre notre dame de la rouge, nous pouvons bénéficier d''un lieu en pleine nature et \r\n		d''animateur expérimentés pour récompenser nos bénévoles en les liants aussi dans des \r\n		expériences qui font réfléchir, qui renforci le groupe qu''est le Petit Peuple et qui tisse des \r\n		liens d''amitié entre eux.', '76.jpg', '13'),
(20, 'Festival d''hiver 2007', 'page.php?page_numero=35', '', '20.jpg', '8'),
